irule:
ltm rule /Common/https_301_redirect.irule {
            when HTTP_REQUEST {
                HTTP::respond 301 Location "https://[HTTP::host][HTTP::uri]"
            }
        }

ltm rule /Common/https_redirect_host_only.irule {
            when HTTP_REQUEST {
               set host [HTTP::host]
               HTTP::respond 302 Location "https://$host/"
            }
        }

ltm rule /Common/mms_no-persist.irule {
    #This irule removes persistence settings for specific urls
when HTTP_REQUEST {
  switch -glob [string tolower [HTTP::uri]] {
    "/internalservices/registration.svc" {
            persist none
    }
}
}
}

ltm rule /Common/embargo-cc-reject.irule {
    when CLIENT_ACCEPTED {
    # Get the country client IP
    switch [whereis [IP::client_addr] country] {
        "CU" -
        "IR" -
        "KP" -
        "SD" -
        "SY" {
            # Reset the TCP connection
            reject
        }
    }
}
}

ltm rule /Common/block.admin.folder.rule {
    #This i-rule is to block anything with /admin for VIP {Requestor : Ashok Kumar }
when HTTP_REQUEST {
    switch -glob [string tolower [HTTP::uri]] {
        "/admin*" { reject }
        default { return }
    }
}}

ltm rule /Common/ah.manage.mcafee.com.access.irule {
#This irule blocks requests to certain services for ah*.manage.mcafee.com for cloudEPO
priority 150
when HTTP_REQUEST {
  switch -glob [string tolower [HTTP::uri]] {
    "/spipe*"
            -
    "/software*"
            -
    "/config*"
            -
    "/redir*"
            -
    "/policy*"
            {pool [LB::server pool]}
    default {
            log local0. "ah.manage.mcafee.com.access.irule dropped connection from [IP::client_addr] to [HTTP::uri]"
            discard }
    }
}}

ltm rule /Common/artifactory-lvs.redirection.irule {
    when HTTP_REQUEST {
switch -glob [HTTP::path] {
"/"  {
     HTTP::redirect "https://artifactory-lvs.corpzone.internalzone.com/artifactory/webapp/"
         }
"/artifactory" {
     HTTP::redirect "https://artifactory-lvs.corpzone.internalzone.com/artifactory/webapp/"
         }
"/artifactory/" {
     HTTP::redirect "https://artifactory-lvs.corpzone.internalzone.com/artifactory/webapp/"
         }
"/artifactory/webapp" {
         HTTP::redirect "https://artifactory-lvs.corpzone.internalzone.com/artifactory/webapp/"
         }
"/v1" {
     HTTP::redirect "https://artifactory-lvs.corpzone.internalzone.com/artifactory/api/docker/docker-local/v1"
         }
"/v2" {
     HTTP::redirect "https://artifactory-lvs.corpzone.internalzone.com/artifactory/api/docker/docker-local/v2"
         }
   }}}

ltm rule /Common/service.mcafee.com.access.irule {
    when HTTP_REQUEST {
     switch -glob [string tolower [HTTP::uri]] {
            "/webcenter/system/*" -
            "/webcenter/portal/system/*" -
            "/webcenter/portal/cp/system/*" -
            "/cs/login/login.htm" -
            "/cs/idcplg*" {
                if { not (( [class match [IP::client_addr] equals mcafee_ips]) || ([class match [IP::client_addr] equals intel_ips])) } {
                log local0. "block [IP::client_addr] to [HTTP::host][HTTP::uri]"
                discard
                }
            }
     }
}}

